export function InicializarOwlCarousel() {
    $(document).ready(function () {
        const carouselListaFilmes = $("#carousel-lista-filmes");

        carouselListaFilmes.owlCarousel({
            loop: true,
            margin: 100,
            nav: false,
            dots: false,
            autoplay: false,
            center: true,
            navText: ["<button class='owl-prev d-none'></button>", "<button class='owl-next btn text-danger'><i class='fs-5 bi bi-chevron-right'></i></button>"],
            navContainer: '.sessao-dicas-rapidas .lista-categorias .navegacao-carrousel',
            responsive: {
                0: {
                    items: 1
                },
                600: {
                    items: 1
                },
                1000: {
                    items: 3
                }
            }
        })
    });
}
