import { Component, OnInit } from "@angular/core";
import { NavigationEnd, Router } from "@angular/router";
import { filter } from "rxjs";

const url = "https://swapi.py4e.com/api/films/";

@Component({
  selector: "app-detalhes-filme",
  templateUrl: "./detalhes-filme.component.html",
  styleUrls: ["./detalhes-filme.component.scss"],
})
export class DetalhesFilmeComponent implements OnInit {
  urlAtual?: String;

  urlImagemFilme = {
    1: "../assets/imagens/a_new_hope/frame_a_new_hope.jpeg",
    2: "Artigos",
    3: "Detalhes Artigo",
    4: "Categorias",
    5: "Dicas Rápidas",
    6: "Vídeos",
    7: "Webinar",
  };

  filme: any;
  imagemFilme?: String;

  constructor(private router: Router) {
    router.events
      .pipe(filter((event: any) => event instanceof NavigationEnd))
      .subscribe((x) => (this.urlAtual = x.url));
  }
  ngOnInit() {
    this._obterFilme();
  }
  async _obterFilme() {
    const response = await fetch(url + this.urlAtual)
      .then((res) => res.json())
      .then((data) => data);
    this.filme.push(response);
    console.log(this.filme);
    this._atribuirImagemParaFilmeSelecionado(this.urlAtual || '');
  }

  _atribuirImagemParaFilmeSelecionado(id: Number) {
    this.imagemFilme = this.urlImagemFilme[id as keyof object];
    debugger;
  }
}
